// Parse "output" string into JSON
const inputArray = JSON.parse(items[0].json.output);

let results = [];

// Helper: normalize values into arrays
function toArray(val) {
  if (!val) return [];
  if (Array.isArray(val)) return val;
  if (typeof val === "string") {
    return val.split(",").map(v => v.trim()).filter(v => v.length > 0);
  }
  return [val];
}

for (const entry of inputArray) {
  const params = entry.salesNavigatorParams;

  const titles = toArray(params.current_titles);
  const companies = params.current_companies || "";
  const keywords = toArray(params.keywords);
  const sizes = toArray(params.company_sizes);
  const location = params.location || "";
  const industry = params.industry || "";

  const safeTitles = titles.length ? titles : [""];
  const safeKeywords = keywords.length ? keywords : [""];
  const safeSizes = sizes.length ? sizes : [""];

  for (const title of safeTitles) {
    for (const keyword of safeKeywords) {
      for (const size of safeSizes) {
        results.push({
          json: {
            title,
            company: companies,
            keyword,
            company_size: size,
            location,
            industry,
          },
        });
      }
    }
  }
}

return results;

