import json

results = []

for item in items:
    raw_output = item['json'].get('output', "")

    if not raw_output:
        continue

    try:
        # First decode
        first_pass = json.loads(raw_output)
    except Exception as e:
        # Try to auto-fix: add closing quotes/brackets if missing
        fixed_output = raw_output.strip()
        if not fixed_output.endswith('}]'):
            fixed_output += "\"}]"
        try:
            first_pass = json.loads(fixed_output)
        except Exception:
            continue

    # Ensure list
    if isinstance(first_pass, dict):
        parsed_output = [first_pass]
    else:
        parsed_output = first_pass

    for obj in parsed_output:
        results.append({"json": obj})

# Always return something so n8n continues
if not results:
    results = [{"json": {"error": "No valid data parsed", "raw": raw_output[:200]}}]

return results

