let results = [];

for (const item of items) {
  const raw_output = item.output || item.json?.output;

  if (typeof raw_output === "object") {
    if (Array.isArray(raw_output)) {
      results.push(...raw_output.map(obj => ({
        json: obj
      })));
    } else {
      results.push({
        json: raw_output
      });
    }
  } else if (typeof raw_output === "string") {
    let parsed;
    try {
      parsed = JSON.parse(raw_output);
    } catch (error) {
      // fallback: extract Product Summary and URN from raw string
      const productSummaryMatch = raw_output.match(/"Product Summary"\s*:\s*"([^"]*)"/s);
      const urnValueMatch = raw_output.match(/"urn"\s*:\s*{[^}]*"value"\s*:\s*"([^"]+)"[^}]*}/);

      // return in valid format even on error
      results.push({
        json: {
          error: "Failed to parse JSON",
          product_summary: productSummaryMatch ? productSummaryMatch[1] : null,
          urn_value: urnValueMatch ? urnValueMatch[1] : null
        }
      });
      continue;
    }

    if (Array.isArray(parsed)) {
      results.push(...parsed.map(obj => ({
        json: obj
      })));
    } else {
      results.push({
        json: parsed
      });
    }
  }
}

return results;

