// Goal: extract and parse output JSON from each item

return items.map(item => {
  let raw = item.json.output || "";
  let cleaned = raw
    .replace(/```json/g, "")  // remove starting ```json
    .replace(/```/g, "")      // remove ending ```
    .trim();

  let parsed;
  try {
    parsed = JSON.parse(cleaned); // Parse cleaned string into JSON
  } catch (e) {
    parsed = { error: "Invalid JSON in output", raw: raw };
  }

  return { json: parsed };
});

return items.map(item => {
