  let id = null;
  let aiAgentOutput = null;
  let error = null;

  try {
    // Try parsing JSON first
    const parsed = JSON.parse(raw);
    id = parsed.id ?? null;
    aiAgentOutput = parsed["ai agent output"] ?? null;
  } catch (e) {
    // JSON parsing failed - fallback to regex extraction

    error = "Invalid JSON format in output";

    // Extract id (number after id:)
