    if (idMatch) {
      id = Number(idMatch[1]);
    }

    // Extract ai agent output (text inside quotes after ai agent output :)
    // This regex captures everything between the first pair of double quotes after "ai agent output :"
    const outputMatch = raw.match(/ai agent output\s*:\s*"(.*?)"/is);
    if (outputMatch) {
      aiAgentOutput = outputMatch[1];
    }
  }

  return {
    json: {
      id,
      aiAgentOutput,
      error,
      rawOutput: error ? raw : null
    }
  };
});

